import React, { useEffect, useState } from "react";
import axios from "axios";

function Home({ user }) {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState("");
  const [commentText, setCommentText] = useState("");

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/posts");
      setPosts(res.data);
    } catch (err) {
      console.error("Error fetching posts:", err);
    }
  };

  const handleCreatePost = async () => {
    if (!newPost.trim()) return;
    try {
      await axios.post("http://localhost:5000/api/posts", {
        userId: user?._id,
        text: newPost,
      });
      setNewPost("");
      fetchPosts();
    } catch (err) {
      console.error("Error creating post:", err);
    }
  };

  const handleLike = async (postId) => {
    try {
      await axios.put(`http://localhost:5000/api/posts/${postId}/like`, {
        userId: user?._id,
      });
      fetchPosts();
    } catch (err) {
      console.error("Error liking post:", err);
    }
  };

  const handleComment = async (postId) => {
    if (!commentText.trim()) return;
    try {
      await axios.put(`http://localhost:5000/api/posts/${postId}/comment`, {
        userId: user?._id,
        text: commentText,
      });
      setCommentText("");
      fetchPosts();
    } catch (err) {
      console.error("Error commenting:", err);
    }
  };

  return (
    <div className="container">
      {user && (
        <div className="post-card">
          <h3>What's on your mind?</h3>
          <textarea
            rows="3"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
            placeholder="Write something..."
            style={{ width: "100%", padding: "10px", borderRadius: "8px" }}
          />
          <button onClick={handleCreatePost}>Post</button>
        </div>
      )}

      <h2>All Posts</h2>
      {posts.map((post) => (
        <div key={post._id} className="post-card">
          <h3>{post.user?.username || "Anonymous"}</h3>
          <p>{post.text}</p>

          <p>Likes: {post.likes.length}</p>
          {user && (
            <button onClick={() => handleLike(post._id)}>üëç Like/Unlike</button>
          )}

          <div style={{ marginTop: "10px" }}>
            <strong>Comments:</strong>
            {post.comments.map((c, i) => (
              <p key={i}>
                <b>{c.user?.username || "Anonymous"}:</b> {c.text}
              </p>
            ))}
          </div>

          {user && (
            <div style={{ marginTop: "10px" }}>
              <input
                type="text"
                placeholder="Comment..."
                value={commentText}
                onChange={(e) => setCommentText(e.target.value)}
              />
              <button onClick={() => handleComment(post._id)}>Comment</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default Home;
