import React, { useState, useEffect } from "react";
import axios from "axios";

function Home({ user }) {
  const [posts, setPosts] = useState([]);
  const [postText, setPostText] = useState("");

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const res = await axios.get("http://localhost:5000/posts");
      setPosts(res.data.reverse()); // show latest posts first
    } catch (err) {
      console.error(err);
    }
  };

  const handlePost = async () => {
    if (!user) return alert("Please login to post.");
    if (!postText) return alert("Cannot post empty content.");

    try {
      await axios.post("http://localhost:5000/posts", {
        userId: user._id, // user id
        username: user.username, // optional, for display
        content: postText,
      });
      setPostText("");
      fetchPosts();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Welcome, {user ? user.username : "Guest"}</h2>

      {user && (
        <div style={{ marginBottom: "20px" }}>
          <textarea
            placeholder="What's on your mind?"
            value={postText}
            onChange={(e) => setPostText(e.target.value)}
            style={{ width: "100%", padding: "10px" }}
          />
          <button onClick={handlePost} style={{ marginTop: "10px" }}>
            Post
          </button>
        </div>
      )}

      <h3>All Posts</h3>
      {posts.map((p) => (
        <div key={p._id} style={styles.postCard}>
          <strong>{p.username}</strong>
          <p>{p.content}</p>
          <small>Likes: {p.likes.length}</small>
        </div>
      ))}
    </div>
  );
}

const styles = {
  postCard: {
    border: "1px solid #ccc",
    padding: "10px",
    borderRadius: "5px",
    marginBottom: "10px",
  },
};

export default Home;
