import React from 'react';

const Navbar = ({ setPage, user, handleLogout }) => {
  return (
    <nav style={styles.nav}>
      <h2 style={{ color: 'white' }}>Social Media App</h2>
      <div>
        {user ? (
          <>
            <span style={styles.welcome}>Welcome, {user.username}</span>
            <button style={styles.logoutBtn} onClick={handleLogout}>
              Logout
            </button>
          </>
        ) : (
          <>
            <button style={styles.btn} onClick={() => setPage('login')}>Login</button>
            <button style={styles.btn} onClick={() => setPage('register')}>Register</button>
          </>
        )}
      </div>
    </nav>
  );
};

const styles = {
  nav: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px 20px', background: '#0077b6' },
  btn: { margin: '0 5px', padding: '5px 10px', cursor: 'pointer' },
  logoutBtn: { marginLeft: '10px', padding: '5px 10px', background: 'red', color: 'white', cursor: 'pointer', border: 'none', borderRadius: '4px' },
  welcome: { color: 'white', marginRight: '10px' }
};

export default Navbar;
