import React, { useState } from "react";
import { API } from "../api";

function Post({ post, user, refreshPosts }) {
  const [comment, setComment] = useState("");

  const handleLike = async () => {
    if (!user) return alert("Login first");
    try {
      await API.put(`/posts/${post._id}/like`, { userId: user._id });
      refreshPosts();
    } catch (err) {
      console.error("Error liking post:", err);
    }
  };

  const handleComment = async () => {
    if (!user) return alert("Login first");
    if (comment.trim() === "") return;
    try {
      await API.put(`/posts/${post._id}/comment`, {
        userId: user._id,
        text: comment,
      });
      setComment("");
      refreshPosts();
    } catch (err) {
      console.error("Error commenting:", err);
    }
  };

  return (
    <div
      style={{
        border: "1px solid #ccc",
        borderRadius: "8px",
        margin: "15px 0",
        padding: "15px",
        backgroundColor: "#f9f9f9",
      }}
    >
      {/* ‚úÖ Post Author */}
      <p>
        <b>{post.user?.username || "Unknown"}:</b> {post.text}
      </p>

      {/* ‚úÖ Like Section */}
      <p>
        Likes: {post.likes?.length || 0}{" "}
        <button onClick={handleLike}>üëç Like/Unlike</button>
      </p>

      {/* ‚úÖ Comment Input */}
      <div style={{ marginTop: "10px" }}>
        <input
          type="text"
          value={comment}
          placeholder="Write a comment..."
          onChange={(e) => setComment(e.target.value)}
          style={{ padding: "5px", width: "70%" }}
        />
        <button onClick={handleComment} style={{ marginLeft: "5px" }}>
          üí¨ Comment
        </button>
      </div>

      {/* ‚úÖ Comments Section */}
      <div style={{ marginTop: "10px" }}>
        {post.comments?.map((c, i) => (
          <p
            key={i}
            style={{
              background: "#fff",
              padding: "6px",
              borderRadius: "5px",
              margin: "5px 0",
              border: "1px solid #ddd",
            }}
          >
            <b>{c.user?.username || "Unknown"}:</b> {c.text}
          </p>
        ))}
      </div>
    </div>
  );
}

export default Post;
