import React, { useState } from 'react';
import { API } from '../api';

function Post({ post, user, refreshPosts }) {
  const [comment, setComment] = useState('');

  const handleLike = async () => {
    if (!user) return alert("Login first");
    try {
      await API.put(`/posts/${post._id}/like`, { userId: user._id });
      refreshPosts();
    } catch (err) {
      console.error(err);
    }
  };

  const handleComment = async () => {
    if (!user) return alert("Login first");
    if (comment.trim() === "") return;
    try {
      await API.put(`/posts/${post._id}/comment`, { userId: user._id, text: comment });
      setComment('');
      refreshPosts();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ border: '1px solid gray', margin: '10px 0', padding: '10px', borderRadius: '8px', background: '#f9f9f9' }}>
      <p><b>{post.user?.username || 'Unknown'}:</b> {post.content}</p>
      <p>Likes: {post.likes.length} <button onClick={handleLike}>Like/Unlike</button></p>

      <div>
        <input 
          type="text" 
          value={comment} 
          placeholder="Comment..." 
          onChange={e => setComment(e.target.value)} 
          style={{ width: '80%', padding: '5px' }}
        />
        <button onClick={handleComment} style={{ marginLeft: '5px' }}>Comment</button>
      </div>

      <div style={{ marginTop: '10px' }}>
        {post.comments.map((c, i) => (
          <p key={i}><b>{c.user?.username || c.userId}:</b> {c.text}</p>
        ))}
      </div>
    </div>
  );
}

export default Post;
