import React, { useState } from "react";
import axios from "axios";

const Login = ({ setUser, setPage }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      const res = await axios.post("http://localhost:5000/auth/login", { username, password });
      setUser(res.data); // store user object
      setPage("home");
    } catch (err) {
      console.error(err.response || err);
      alert("Invalid credentials!");
    }
  };

  return (
    <div style={{ width: "300px", margin: "50px auto" }}>
      <h3>Login</h3>
      <input placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} style={{ width: "100%", marginBottom: "5px", padding: "5px" }} />
      <input placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} style={{ width: "100%", marginBottom: "5px", padding: "5px" }} />
      <button onClick={handleLogin} style={{ width: "100%", padding: "8px" }}>Login</button>
    </div>
  );
};

export default Login;
