import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Home = ({ user }) => {
  const [postText, setPostText] = useState('');
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/posts');
      setPosts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handlePost = async () => {
    if (!postText.trim()) return;
    try {
      const res = await axios.post('http://localhost:5000/posts', {
        userId: user._id,
        text: postText,
      });
      setPostText('');
      setPosts([res.data, ...posts]);
    } catch (err) {
      console.error('Error creating post:', err.response || err);
    }
  };

  return (
    <div style={styles.container}>
      {user && (
        <div style={styles.createPost}>
          <textarea
            placeholder="What's on your mind?"
            value={postText}
            onChange={(e) => setPostText(e.target.value)}
            style={styles.textarea}
          />
          <button onClick={handlePost} style={styles.postBtn}>Post</button>
        </div>
      )}

      <h3>All Posts</h3>
      {posts.map((post) => (
        <div key={post._id} style={styles.post}>
          <strong>{post.user.username}</strong>
          <p>{post.text}</p>
        </div>
      ))}
    </div>
  );
};

const styles = {
  container: { width: '60%', margin: '20px auto', fontFamily: 'Arial, sans-serif' },
  createPost: { marginBottom: '20px', display: 'flex', flexDirection: 'column' },
  textarea: { padding: '10px', minHeight: '60px', marginBottom: '10px', borderRadius: '5px', border: '1px solid #ccc' },
  postBtn: { width: '100px', padding: '8px', border: 'none', borderRadius: '5px', background: '#0077b6', color: 'white', cursor: 'pointer' },
  post: { border: '1px solid #ccc', padding: '10px', borderRadius: '5px', marginBottom: '10px', background: '#f1f1f1' }
};

export default Home;
