import React from "react";

function Navbar({ setPage, user, setUser }) {
  const handleLogout = () => {
    setUser(null);
    setPage("login");
  };

  return (
    <nav
      style={{
        padding: "10px",
        background: "#1976d2",
        color: "white",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
      }}
    >
      <h2 style={{ cursor: "pointer" }} onClick={() => setPage("home")}>
        Social Media App
      </h2>
      <div>
        {!user ? (
          <>
            <button onClick={() => setPage("login")} style={btnStyle}>
              Login
            </button>
            <button onClick={() => setPage("register")} style={btnStyle}>
              Register
            </button>
          </>
        ) : (
          <>
            <span style={{ marginRight: "10px" }}>Welcome, {user.username}</span>
            <button onClick={handleLogout} style={btnStyle}>
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
}

const btnStyle = {
  padding: "5px 10px",
  margin: "0 5px",
  background: "white",
  color: "#1976d2",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};

export default Navbar;
