import React, { useState } from 'react';
import { API } from '../api';

function Post({ post, user, refreshPosts }) {
  const [comment, setComment] = useState('');

  const handleLike = async () => {
    if(!user) return alert("Login first");
    await API.put(`/posts/${post._id}/like`, { userId: user._id });
    refreshPosts();
  };

  const handleComment = async () => {
    if(!user) return alert("Login first");
    if(comment.trim() === "") return;
    await API.put(`/posts/${post._id}/comment`, { userId: user._id, text: comment });
    setComment('');
    refreshPosts();
  };

  return (
    <div style={{border:'1px solid gray', margin:'10px', padding:'10px'}}>
      <p>{post.content}</p>
      <p>Likes: {post.likes.length} <button onClick={handleLike}>Like/Unlike</button></p>
      <div>
        <input type="text" value={comment} placeholder="Comment..." onChange={e => setComment(e.target.value)} />
        <button onClick={handleComment}>Comment</button>
      </div>
      <div>
        {post.comments.map((c, i) => <p key={i}><b>{c.userId}:</b> {c.text}</p>)}
      </div>
    </div>
  );
}

export default Post;
