import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = ({ user }) => {
  const [postText, setPostText] = useState("");
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const res = await axios.get("http://localhost:5000/posts");
      setPosts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handlePost = async () => {
    if (!postText.trim()) return alert("Type something!");
    try {
      const res = await axios.post("http://localhost:5000/posts", {
        userId: user._id,
        text: postText,
      });
      setPosts([res.data, ...posts]);
      setPostText("");
    } catch (err) {
      console.error(err.response || err);
      alert("Failed to post!");
    }
  };

  return (
    <div style={{ width: "60%", margin: "20px auto" }}>
      {user && (
        <div style={{ marginBottom: "20px" }}>
          <textarea
            value={postText}
            onChange={(e) => setPostText(e.target.value)}
            placeholder="What's on your mind?"
            style={{ width: "100%", minHeight: "60px", padding: "10px" }}
          />
          <button onClick={handlePost} style={{ marginTop: "5px", padding: "8px 15px" }}>
            Post
          </button>
        </div>
      )}

      <h3>All Posts</h3>
      {posts.map((post) => (
        <div key={post._id} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px" }}>
          <strong>{post.user.username}</strong>
          <p>{post.text}</p>
        </div>
      ))}
    </div>
  );
};

export default Home;
