import React from "react";

const Navbar = ({ setPage, user, setUser }) => {
  return (
    <nav style={styles.navbar}>
      <h1 style={styles.logo}>Social Media App</h1>
      {user ? (
        <div>
          <span style={styles.welcome}>Welcome, {user.username}</span>
          <button
            style={styles.logoutBtn}
            onClick={() => {
              setUser(null);
              setPage("login");
            }}
          >
            Logout
          </button>
        </div>
      ) : (
        <div>
          <button style={styles.btn} onClick={() => setPage("login")}>
            Login
          </button>
          <button style={styles.btn} onClick={() => setPage("register")}>
            Register
          </button>
        </div>
      )}
    </nav>
  );
};

const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    padding: "15px 30px",
    backgroundColor: "#1976d2",
    color: "#fff",
    alignItems: "center",
    boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
  },
  logo: { margin: 0 },
  welcome: { marginRight: "15px", fontWeight: "bold" },
  logoutBtn: {
    padding: "6px 12px",
    backgroundColor: "#f44336",
    border: "none",
    borderRadius: "4px",
    color: "#fff",
    cursor: "pointer",
  },
  btn: {
    marginLeft: "10px",
    padding: "6px 12px",
    backgroundColor: "#4caf50",
    border: "none",
    borderRadius: "4px",
    color: "#fff",
    cursor: "pointer",
  },
};

export default Navbar;
