import React from "react";

const Navbar = ({ user, setPage, setUser }) => {
  const handleLogout = () => {
    setUser(null);
    setPage("login");
  };

  return (
    <nav style={{ display: "flex", justifyContent: "space-between", padding: "10px 20px", background: "#1976d2", color: "white" }}>
      <h2 onClick={() => setPage("home")} style={{ cursor: "pointer" }}>Social Media App</h2>
      <div>
        {user ? (
          <>
            <span style={{ marginRight: "10px" }}>Welcome, {user.username}</span>
            <button onClick={handleLogout} style={{ padding: "5px 10px" }}>Logout</button>
          </>
        ) : (
          <>
            <button onClick={() => setPage("login")} style={{ marginRight: "5px" }}>Login</button>
            <button onClick={() => setPage("register")}>Register</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
